// src/swiper-css.d.ts

/**
 * Declares Swiper's CSS modules to TypeScript to prevent import errors.
 * This tells TypeScript that these modules exist, even though they aren't TS/JS files.
 */

declare module "swiper/css";
declare module "swiper/css/navigation";
declare module "swiper/css/pagination";
declare module "swiper/css/effect-coverflow";
